INSERT INTO `modx_site_plugins` VALUES (1,0,0,'Ace','Ace code editor plugin for MODx Revolution',0,0,0,'/**\n * Ace Source Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnSnipFormPrerender,\n * OnTempFormPrerender, OnChunkFormPrerender, OnPluginFormPrerender,\n * OnFileCreateFormPrerender, OnFileEditFormPrerender, OnDocFormPrerender\n *\n * @author Danil Kostin <danya.postfactum(at)gmail.com>\n *\n * @package ace\n *\n * @var array $scriptProperties\n * @var Ace $ace\n */\nif ($modx->event->name == \'OnRichTextEditorRegister\') {\n    $modx->event->output(\'Ace\');\n    return;\n}\n\nif ($modx->getOption(\'which_element_editor\', null, \'Ace\') !== \'Ace\') {\n    return;\n}\n\n$ace = $modx->getService(\'ace\', \'Ace\', $modx->getOption(\'ace.core_path\', null, $modx->getOption(\'core_path\').\'components/ace/\').\'model/ace/\');\n$ace->initialize();\n\n$extensionMap = array(\n    \'tpl\'   => \'text/x-smarty\',\n    \'htm\'   => \'text/html\',\n    \'html\'  => \'text/html\',\n    \'css\'   => \'text/css\',\n    \'scss\'  => \'text/x-scss\',\n    \'less\'  => \'text/x-less\',\n    \'svg\'   => \'image/svg+xml\',\n    \'xml\'   => \'application/xml\',\n    \'xsl\'   => \'application/xml\',\n    \'js\'    => \'application/javascript\',\n    \'json\'  => \'application/json\',\n    \'php\'   => \'application/x-php\',\n    \'sql\'   => \'text/x-sql\',\n    \'md\'    => \'text/x-markdown\',\n    \'txt\'   => \'text/plain\',\n    \'twig\'  => \'text/x-twig\'\n);\n\n// Defines wether we should highlight modx tags\n$modxTags = false;\nswitch ($modx->event->name) {\n    case \'OnSnipFormPrerender\':\n        $field = \'modx-snippet-snippet\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnTempFormPrerender\':\n        $field = \'modx-template-content\';\n        $modxTags = true;\n\n        switch (true) {\n            case $modx->getOption(\'twiggy_class\'):\n                $mimeType = \'text/x-twig\';\n                break;\n            case $modx->getOption(\'pdotools_fenom_parser\'):\n                $mimeType = \'text/x-smarty\';\n                break;\n            default:\n                $mimeType = \'text/html\';\n                break;\n        }\n\n        break;\n    case \'OnChunkFormPrerender\':\n        $field = \'modx-chunk-snippet\';\n        if ($modx->controller->chunk && $modx->controller->chunk->isStatic()) {\n            $extension = pathinfo($modx->controller->chunk->getSourceFile(), PATHINFO_EXTENSION);\n            $mimeType = isset($extensionMap[$extension]) ? $extensionMap[$extension] : \'text/plain\';\n        } else {\n            $mimeType = \'text/html\';\n        }\n        $modxTags = true;\n\n        switch (true) {\n            case $modx->getOption(\'twiggy_class\'):\n                $mimeType = \'text/x-twig\';\n                break;\n            case $modx->getOption(\'pdotools_fenom_default\'):\n                $mimeType = \'text/x-smarty\';\n                break;\n            default:\n                $mimeType = \'text/html\';\n                break;\n        }\n\n        break;\n    case \'OnPluginFormPrerender\':\n        $field = \'modx-plugin-plugincode\';\n        $mimeType = \'application/x-php\';\n        break;\n    case \'OnFileCreateFormPrerender\':\n        $field = \'modx-file-content\';\n        $mimeType = \'text/plain\';\n        break;\n    case \'OnFileEditFormPrerender\':\n        $field = \'modx-file-content\';\n        $extension = pathinfo($scriptProperties[\'file\'], PATHINFO_EXTENSION);\n        $mimeType = isset($extensionMap[$extension])\n            ? $extensionMap[$extension]\n            : \'text/plain\';\n        $modxTags = $extension == \'tpl\';\n        break;\n    case \'OnDocFormPrerender\':\n        if (!$modx->controller->resourceArray) {\n            return;\n        }\n        $field = \'ta\';\n        $mimeType = $modx->getObject(\'modContentType\', $modx->controller->resourceArray[\'content_type\'])->get(\'mime_type\');\n\n        switch (true) {\n            case $mimeType == \'text/html\' && $modx->getOption(\'twiggy_class\'):\n                $mimeType = \'text/x-twig\';\n                break;\n            case $mimeType == \'text/html\' && $modx->getOption(\'pdotools_fenom_parser\'):\n                $mimeType = \'text/x-smarty\';\n                break;\n        }\n\n        if ($modx->getOption(\'use_editor\')){\n            $richText = $modx->controller->resourceArray[\'richtext\'];\n            $classKey = $modx->controller->resourceArray[\'class_key\'];\n            if ($richText || in_array($classKey, array(\'modStaticResource\',\'modSymLink\',\'modWebLink\',\'modXMLRPCResource\'))) {\n                $field = false;\n            }\n        }\n        $modxTags = true;\n        break;\n    default:\n        return;\n}\n\n$modxTags = (int) $modxTags;\n$script = \'\';\nif ($field) {\n    $script .= \"MODx.ux.Ace.replaceComponent(\'$field\', \'$mimeType\', $modxTags);\";\n}\n\nif ($modx->event->name == \'OnDocFormPrerender\' && !$modx->getOption(\'use_editor\')) {\n    $script .= \"MODx.ux.Ace.replaceTextAreas(Ext.query(\'.modx-richtext\'));\";\n}\n\nif ($script) {\n    $modx->controller->addHtml(\'<script>Ext.onReady(function() {\' . $script . \'});</script>\');\n}',0,NULL,0,'',0,'ace/elements/plugins/ace.plugin.php'),(2,0,0,'CKEditor','CKEditor WYSIWYG editor plugin for MODx Revolution',0,0,0,'/**\n * CKEditor WYSIWYG Editor Plugin\n *\n * Events: OnManagerPageBeforeRender, OnRichTextEditorRegister, OnRichTextEditorInit, OnRichTextBrowserInit\n *\n * @var modX $modx\n * @author Danil Kostin <danya.postfactum(at)gmail.com>\n *\n * @package ckeditor\n */\n$enabled = $modx->getOption(\'which_editor\', null, \'CKEditor\') == \'CKEditor\' && $modx->getOption(\'use_editor\', null, true);\n\nswitch ($modx->event->name) {\n    case \'OnRichTextEditorRegister\':\n        $modx->event->output(\'CKEditor\');\n        break;\n    case \'OnManagerPageBeforeRender\':\n        if ($enabled) {\n            /** @var CKEditor $ckeditor */\n            $ckeditor = $modx->getService(\'ckeditor\', \'CKEditor\', $modx->getOption(\'ckeditor.core_path\', null, $modx->getOption(\'core_path\').\'components/ckeditor/\') . \'model/ckeditor/\');\n            $ckeditor->initialize();\n        }\n        break;\n    case \'OnRichTextEditorInit\':\n        break;\n    case \'OnRichTextBrowserInit\':\n        if ($enabled) {\n            $funcNum = $_REQUEST[\'CKEditorFuncNum\'];\n            $modx->event->output(\"function(data){\n                window.parent.opener.CKEDITOR.tools.callFunction({$funcNum}, data.fullRelativeUrl);\n            }\");\n        }\n        break;\n}\n\nreturn;',0,NULL,0,'',1,'ckeditor/elements/plugins/ckeditor.plugin.php'),(3,0,0,'ImagePlus','Image+ runtime hooks - registers custom TV input & output types and includes javascripts on document edit pages.',0,1,0,'/**\n * Image+ runtime hooks\n * Registers custom TV input & output types and includes javascripts on\n * document edit pages so that the TV can be used from within other extras\n * (i.e. MIGX, Collections)\n *\n * Copyright 2013-2015 by Alan Pich <alan.pich@gmail.com>\n * Copyright 2015 by Thomas Jakobi <thomas.jakobi@partout.info>\n *\n * @package imageplus\n * @subpackage plugin\n *\n * @author Alan Pich <alan.pich@gmail.com>\n * @author Thomas Jakobi <thomas.jakobi@partout.info>\n * @copyright Alan Pich 2013-2015\n * @copyright Thomas Jakobi 2015\n *\n * @event   OnTVInputRenderList\n * @event   OnTVOutputRenderList\n * @event   OnTVInputPropertiesList\n * @event   OnTVOutputRenderPropertiesList\n * @event   OnDocFormRender\n */\n\n$corePath = $modx->getOption(\'imageplus.core_path\', null, $modx->getOption(\'core_path\') . \'components/imageplus/\');\n$imageplus = $modx->getService(\'imageplus\', \'ImagePlus\', $corePath . \'model/imageplus/\', array(\n    \'core_path\' => $corePath\n));\n\nswitch ($modx->event->name) {\n    case \'OnManagerPageBeforeRender\':\n        $modx->controller->addLexiconTopic(\'imageplus:default\');\n        break;\n    case \'OnTVInputRenderList\':\n        $modx->event->output($corePath . \'elements/tv/input/\');\n        break;\n    case \'OnTVOutputRenderList\':\n        $modx->event->output($corePath . \'elements/tv/output/\');\n        break;\n    case \'OnTVInputPropertiesList\':\n        $modx->event->output($corePath . \'elements/tv/input/options/\');\n        break;\n    case \'OnTVOutputRenderPropertiesList\':\n        $modx->event->output($corePath . \'elements/tv/output/options/\');\n        break;\n    case \'OnDocFormRender\':\n        $imageplus->includeScriptAssets();\n        break;\n};',0,'a:0:{}',0,'',0,''),(4,1,0,'miniShop2','',0,2,0,'switch ($modx->event->name) {\n\n    case \'OnMODXInit\':\n        /** @var miniShop2 $miniShop2 */\n        if ($miniShop2 = $modx->getService(\'miniShop2\')) {\n            $miniShop2->plugins->loadMap();\n        }\n        break;\n\n    case \'OnHandleRequest\':\n    case \'OnLoadWebDocument\':\n        $isAjax = !empty($_SERVER[\'HTTP_X_REQUESTED_WITH\']) && $_SERVER[\'HTTP_X_REQUESTED_WITH\'] == \'XMLHttpRequest\';\n\n        if (empty($_REQUEST[\'ms2_action\']) || ($isAjax && $modx->event->name != \'OnHandleRequest\') || (!$isAjax && $modx->event->name != \'OnLoadWebDocument\')) {\n            return;\n        }\n        $action = trim($_REQUEST[\'ms2_action\']);\n        $ctx = !empty($_REQUEST[\'ctx\']) ? (string)$_REQUEST[\'ctx\'] : \'web\';\n        if ($ctx != \'web\') {\n            $modx->switchContext($ctx);\n        }\n\n        /* @var miniShop2 $miniShop2 */\n        $miniShop2 = $modx->getService(\'minishop2\');\n        $miniShop2->initialize($ctx, array(\'json_response\' => $isAjax));\n        if (!($miniShop2 instanceof miniShop2)) {\n            @session_write_close();\n            exit(\'Could not initialize miniShop2\');\n        }\n\n        switch ($action) {\n            case \'cart/add\':\n                $response = $miniShop2->cart->add(@$_POST[\'id\'], @$_POST[\'count\'], @$_POST[\'options\']);\n                break;\n            case \'cart/change\':\n                $response = $miniShop2->cart->change(@$_POST[\'key\'], @$_POST[\'count\']);\n                break;\n            case \'cart/remove\':\n                $response = $miniShop2->cart->remove(@$_POST[\'key\']);\n                break;\n            case \'cart/clean\':\n                $response = $miniShop2->cart->clean();\n                break;\n            case \'cart/get\':\n                $response = $miniShop2->cart->get();\n                break;\n            case \'order/add\':\n                $response = $miniShop2->order->add(@$_POST[\'key\'], @$_POST[\'value\']);\n                break;\n            case \'order/submit\':\n                $response = $miniShop2->order->submit($_POST);\n                break;\n            case \'order/getcost\':\n                $response = $miniShop2->order->getCost();\n                break;\n            case \'order/getrequired\':\n                $response = $miniShop2->order->getDeliveryRequiresFields(@$_POST[\'id\']);\n                break;\n            case \'order/clean\':\n                $response = $miniShop2->order->clean();\n                break;\n            case \'order/get\':\n                $response = $miniShop2->order->get();\n                break;\n            default:\n                $message = ($_REQUEST[\'ms2_action\'] != $action)\n                    ? \'ms2_err_register_globals\'\n                    : \'ms2_err_unknown\';\n                $response = $miniShop2->error($message);\n        }\n\n        if ($isAjax) {\n            @session_write_close();\n            exit($response);\n        }\n        break;\n\n    case \'OnWebPageInit\':\n        /* @var msCustomerProfile $profile */\n        $referrerVar = $modx->getOption(\'ms2_referrer_code_var\', null, \'msfrom\', true);\n        $cookieVar = $modx->getOption(\'ms2_referrer_cookie_var\', null, \'msreferrer\', true);\n        $cookieTime = $modx->getOption(\'ms2_referrer_time\', null, 86400 * 365, true);\n\n        if (!$modx->user->isAuthenticated() && !empty($_REQUEST[$referrerVar])) {\n            $code = trim($_REQUEST[$referrerVar]);\n            if ($profile = $modx->getObject(\'msCustomerProfile\', array(\'referrer_code\' => $code))) {\n                $referrer = $profile->id;\n                setcookie($cookieVar, $referrer, time() + $cookieTime);\n            }\n        } elseif ($modx->user->isAuthenticated() && !empty($_COOKIE[$cookieVar])) {\n            if ($profile = $modx->getObject(\'msCustomerProfile\', $modx->user->id)) {\n                if (!$profile->get(\'referrer_id\') && $_COOKIE[$cookieVar] != $modx->user->id) {\n                    $profile->set(\'referrer_id\', $_COOKIE[$cookieVar]);\n                    $profile->save();\n                }\n            }\n            setcookie($cookieVar, \'\', time() - $cookieTime);\n        }\n        break;\n\n    case \'msOnChangeOrderStatus\':\n        if (empty($status) || $status != 2) {\n            return;\n        }\n\n        /** @var modUser $user */\n        if ($user = $order->getOne(\'User\')) {\n            $q = $modx->newQuery(\'msOrder\', array(\'type\' => 0));\n            $q->innerJoin(\'modUser\', \'modUser\', array(\'`modUser`.`id` = `msOrder`.`user_id`\'));\n            $q->innerJoin(\'msOrderLog\', \'msOrderLog\', array(\n                \'`msOrderLog`.`order_id` = `msOrder`.`id`\',\n                \'msOrderLog.action\' => \'status\',\n                \'msOrderLog.entry\' => $status,\n            ));\n            $q->where(array(\'msOrder.user_id\' => $user->id));\n            $q->groupby(\'msOrder.user_id\');\n            $q->select(\'SUM(`msOrder`.`cost`)\');\n            if ($q->prepare() && $q->stmt->execute()) {\n                $spent = $q->stmt->fetch(PDO::FETCH_COLUMN);\n                /** @var msCustomerProfile $profile */\n                if ($profile = $modx->getObject(\'msCustomerProfile\', $user->id)) {\n                    $profile->set(\'spent\', $spent);\n                    $profile->save();\n                }\n            }\n        }\n        break;\n}',0,NULL,0,'',1,'core/components/minishop2/elements/plugins/plugin.minishop2.php'),(7,1,0,'Brightfield','',0,0,0,'if ($modx->event->name == \'OnManagerPageBeforeRender\') {\n    if(!$controller->scriptProperties[\'id\']) {\n        return;\n    }\n    $resource = $modx->getObject(\'modResource\', $controller->scriptProperties[\'id\']);\n    if($resource->template != 3) {\n        return;\n    }\n\n    $controller->br = $br = $modx->getService(\'br\',\'Brightfield\', MODX_CORE_PATH . \'components/brightfield/model/brightfield/\', array());\n    $controller->addLexiconTopic(\'brightfield:default\');\n\n    $controller->addJavascript($br->config[\'jsUrl\'].\'mgr/brightfield.js\');\n    $controller->addJavascript($br->config[\'jsUrl\'].\'mgr/widgets/grid.kit.view.js\');\n    $controller->addJavascript($br->config[\'jsUrl\'].\'mgr/widgets/window.codeparser.js\');\n    $controller->addJavascript($br->config[\'jsUrl\'].\'mgr/widgets/grid.kit.js\');\n    $controller->addJavascript($br->config[\'jsUrl\'].\'mgr/widgets/panel.kit.js\');\n\n    $controller->addCss($br->config[\'cssUrl\'].\'mgr/brightfield.css\');\n\n    $scripts = array(\n        \'head\' => \'\n			Brightfield.config = \'.$modx->toJSON($br->config).\';\n			Brightfield.config.kit_id = \"\'.$controller->scriptProperties[\'id\'].\'\";\n			Brightfield.config.connector_url = \"\'.$br->config[\'connectorUrl\'].\'\";\n		\'\n        ,\'tab\' => \'\n            Ext.ComponentMgr.onAvailable(\\\'modx-resource-tabs\\\', function() {\n                this.items.unshift({\n                    title: _(\\\'brightfield_kit\\\')\n                    ,cls: \\\'modx-resource-tab\\\'\n                    ,layout: \\\'form\\\'\n                    ,autoScroll: false\n                    ,forceLayout: true\n                    ,deferredRender: false\n                    ,labelWidth: 200\n                    ,bodyCssClass: \\\'main-wrapper\\\'\n                    ,autoHeight: true\n                    ,defaults: {\n                        border: false\n                        ,msgTarget: \\\'under\\\'\n                    }\n                    ,items: [{\n                        xtype: \\\'br-kit\\\'\n                    }]\n                });\n            });\n            \'\n    );\n\n    $controller->addHtml(\'<script type=\"text/javascript\">\'.implode(\"\\n\", $scripts).\'</script>\');\n}\nif($modx->event->name == \'OnDocFormPrerender\') {\n    $br = $modx->getService(\'br\',\'Brightfield\', MODX_CORE_PATH . \'components/brightfield/model/brightfield/\', array());\n\n    if ($mode !== \'upd\') {\n        return;\n    }\n    if($resource->class_key == \'msCategory\' || $resource->class_key == \'msProduct\') {\n        $modx->regClientStartupScript($br->config[\'jsUrl\'].\'mgr/minishop2/widgets.js\');\n        $modx->regClientCSS($br->config[\'cssUrl\'].\'mgr/brightfield.css\');\n    }\n    if($resource->class_key == \'msProduct\') {\n        $currency_rate[\'eur\'] = floatval($modx->getOption(\'currency_rate_eur\'));\n        $currency_rate[\'usd\'] = floatval($modx->getOption(\'currency_rate_usd\'));\n\n        if (!$modx->getObject(\'msProduct\', $id)) {\n            return;\n        }\n        $modx->regClientStartupScript(\"<script type=\\\"text/javascript\\\">\\n\n            Ext.onReady(function() { if(MODx.loadRTE) MODx.loadRTE(\'content_commercial\'); });\n            Brightfield.currency_rate = \".$modx->toJSON($currency_rate).\";\n        \\n</script>\", true);\n        $modx->regClientStartupScript($br->config[\'jsUrl\'].\'mgr/minishop2/tab.product.options.js\');\n    }\n}\nif ($modx->event->name == \'OnBeforeDocFormSave\') {\n    if($resource->get(\'class_key\') !== \'msProduct\') {\n        return;\n    }\n    if ($mode !== \'upd\') {\n        return;\n    }\n    if(!$resource->get(\'article\')) {\n        $modx->event->output(\'Вы не указали Артикул!\');\n        return;\n    }\n    if(!$resource->get(\'article_shinda\')) {\n        $modx->event->output(\'Вы не указали Артикул SHINDA!\');\n        return;\n    }\n\n    $q = $modx->newQuery(\'msProductData\', array(\'article\' => $resource->get(\'article\') ));\n    $q->where(array(\'id:!=\' => $resource->get(\'id\')));\n    if($modx->getCount(\'msProductData\', $q)) {\n        $modx->event->output(\'Продукт с таким артикулом уже существует\');\n        return;\n    }\n\n    $q = $modx->newQuery(\'msProductData\', array(\'article_shinda\' => $resource->get(\'article_shinda\') ));\n    $q->where(array(\'id:!=\' => $resource->get(\'id\')));\n    if($modx->getCount(\'msProductData\', $q)) {\n        $modx->event->output(\'Продукт с таким артикулом SHINDA уже существует\');\n        return;\n    }\n\n    if(!$resource->get(\'paramDiameter\')) {\n        $resource->set(\'diameter\', null);\n        $resource->set(\'diameter_measure\', null);\n    }\n    if(!$resource->get(\'paramLength\')) {\n        $resource->set(\'length\', null);\n        $resource->set(\'length_measure\', null);\n    }\n    if(!$resource->get(\'paramAngleViewing\')) {\n        $resource->set(\'angle_viewing\', null);\n    }\n    if(!$resource->get(\'paramAngleBend\')) {\n        $resource->set(\'angle_bend\', null);\n    }\n}',0,'a:0:{}',0,'',1,'core/components/brightfield/elements/plugins/plugin.brightfield.php'),(5,1,0,'pdoTools','',0,3,0,'switch ($modx->event->name) {\n\n    case \'OnMODXInit\':\n        $fqn = $modx->getOption(\'pdoTools.class\', null, \'pdotools.pdotools\', true);\n        $path = $modx->getOption(\'pdotools_class_path\', null, MODX_CORE_PATH . \'components/pdotools/model/\', true);\n        $modx->loadClass($fqn, $path, false, true);\n\n        $fqn = $modx->getOption(\'pdoFetch.class\', null, \'pdotools.pdofetch\', true);\n        $path = $modx->getOption(\'pdo